Install-Module burnttoast
Import-Module burnttoast

# Disable "Do Not Disturb" mode (Quiet Hours)
Set-ItemProperty -Path "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\QuietHours" -Name "Enabled" -Value 0

# Send the toast notification
$Time = Get-date -Format t
$Btn = New-BTButton -Content 'Got it!' -arguments 'ok'
$Splat = @{
    Text = 'Zed: Opened Domain Join GUI' , "BUT wait for the default apps to be installed before rebooting please. Started $Time"
    Applogo = 'https://iili.io/H8B8JtI.png'
    Sound = 'IM'
    Button = $Btn
    HeroImage = 'https://iili.io/HU7A5bV.jpg'
}
New-BurntToastNotification @splat 

function JoinButton_Click {
    try {
        $domain = $DomainTextBox.Text
        $username = $UsernameTextBox.Text
        $password = $PasswordBox.SecurePassword
        $credential = New-Object System.Management.Automation.PSCredential($username, $password)

        $result = Add-Computer -DomainName $domain -Credential $credential -Force -PassThru

        $OutputTextBox.Text = $result

    } catch [System.UnauthorizedAccessException] {
        $OutputTextBox.Text = "Error: Access to the remote computer was denied. Check your credentials and try again."
    } catch [System.Management.Automation.RuntimeException] {
        $OutputTextBox.Text = "Error: An error occurred while joining the domain. Check your domain name and try again."
    } catch {
        $OutputTextBox.Text = "Error: $_"
        write-Error "Error: $_"
    }
}

# Load presentation framework assemblies
[Reflection.Assembly]::LoadWithPartialName("PresentationFramework") | Out-Null
Add-Type -AssemblyName PresentationFramework, PresentationCore, WindowsBase, WindowsFormsIntegration

# Create the main window
$window = New-Object System.Windows.Window
$window.Title = 'ez Cloud Deploy | Local Domain Join'
$window.Width = 600
$window.Height = 490
$window.WindowStartupLocation = [System.Windows.WindowStartupLocation]::CenterScreen
$mainGrid = New-Object System.Windows.Controls.Grid
$window.Content = $mainGrid

# Create grid rows
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 70}))
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 30}))
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 80}))
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 50}))
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 200}))
$mainGrid.RowDefinitions.Add((New-Object System.Windows.Controls.RowDefinition -Property @{ Height = new-object System.Windows.GridLength 20}))

# Logo section
$logo = New-Object System.Windows.Controls.Image
$logoStream = [System.IO.MemoryStream]::new([System.Convert]::FromBase64String(''))
$logo.Source = [System.Windows.Media.Imaging.BitmapImage]::new()
$logo.Source.BeginInit()
$logo.Source.StreamSource = $logoStream
$logo.Source.EndInit()
$logo.Margin = New-Object System.Windows.Thickness(10)
$logo.height = 80
$logo.width = 130
$logo.verticalAlignment = [System.Windows.VerticalAlignment]::bottom
$Logo.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Left
$logo.SetValue([System.Windows.Controls.Grid]::RowProperty, 0) 
$mainGrid.Children.Add($logo) | Out-Null

# Title section
$titleText = New-Object System.Windows.Controls.TextBlock
$titleText.Text = "ez Deploy | Local Domain Join"
$titleText.FontSize = 20
$titleText.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Center
$titleText.VerticalAlignment = [System.Windows.VerticalAlignment]::Center
$titleText.SetValue([System.Windows.Controls.Grid]::RowProperty, 0)
$mainGrid.Children.Add($titleText) | Out-Null

# Second logo (avatar) section
$avatarLogo = New-Object System.Windows.Controls.Image
$avatarLogoStream = [System.IO.MemoryStream]::new([System.Convert]::FromBase64String(''))
$avatarLogo.Source = [System.Windows.Media.Imaging.BitmapImage]::new()
$avatarLogo.Source.BeginInit()
$avatarLogo.Source.StreamSource = $avatarLogoStream
$avatarLogo.Source.EndInit()
$avatarLogo.Stretch = [System.Windows.Media.Stretch]::Uniform
$avatarLogo.Margin = New-Object System.Windows.Thickness(10)
$avatarLogo.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Right
$avatarLogo.SetValue([System.Windows.Controls.Grid]::RowProperty, 0)
$mainGrid.Children.Add($avatarLogo) | Out-Null

# Parameter input section
$paramPanel = New-Object System.Windows.Controls.StackPanel
$scrollViewer = New-Object System.Windows.Controls.ScrollViewer
$scrollViewer.Content = $paramPanel
$scrollViewer.VerticalScrollBarVisibility = [System.Windows.Controls.ScrollBarVisibility]::Auto
$scrollViewer.SetValue([System.Windows.Controls.Grid]::RowProperty, 2)
$mainGrid.Children.Add($scrollViewer) | Out-Null

# Domain input
$domainGrid = New-Object System.Windows.Controls.Grid
$domainGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(1, [System.Windows.GridUnitType]::Star) }))
$domainGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(2, [System.Windows.GridUnitType]::Star) }))
$domainLabel = New-Object System.Windows.Controls.Label
$domainLabel.Content = "Domain:"
$domainLabel.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Right
$domainLabel.VerticalAlignment = [System.Windows.VerticalAlignment]::Center
$domainTextBox = New-Object System.Windows.Controls.TextBox
$domainTextBox.Name = "DomainTextBox"
$domainTextBox.Margin = New-Object System.Windows.Thickness(5, 2, 150, 2)
$domainTextBox.SetValue([System.Windows.Controls.Grid]::ColumnProperty, 1)
$domainGrid.Children.Add($domainLabel) | Out-Null
$domainGrid.Children.Add($domainTextBox) | Out-Null
$paramPanel.Children.Add($domainGrid) | Out-Null

# Username input
$usernameGrid = New-Object System.Windows.Controls.Grid
$usernameGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(1, [System.Windows.GridUnitType]::Star) }))
$usernameGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(2, [System.Windows.GridUnitType]::Star) }))
$usernameLabel = New-Object System.Windows.Controls.Label
$usernameLabel.Content = "Username:"
$usernameLabel.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Right
$usernameLabel.VerticalAlignment = [System.Windows.VerticalAlignment]::Center
$usernameTextBox = New-Object System.Windows.Controls.TextBox
$usernameTextBox.Name = "UsernameTextBox"
$usernameTextBox.Margin = New-Object System.Windows.Thickness(5, 2, 150, 2)
$usernameTextBox.SetValue([System.Windows.Controls.Grid]::ColumnProperty, 1)
$usernameGrid.Children.Add($usernameLabel) | Out-Null
$usernameGrid.Children.Add($usernameTextBox) | Out-Null
$paramPanel.Children.Add($usernameGrid) | Out-Null

# Password input
$passwordGrid = New-Object System.Windows.Controls.Grid
$passwordGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(1, [System.Windows.GridUnitType]::Star) }))
$passwordGrid.ColumnDefinitions.Add((New-Object System.Windows.Controls.ColumnDefinition -Property @{ Width = New-Object System.Windows.GridLength(2, [System.Windows.GridUnitType]::Star) }))
$passwordLabel = New-Object System.Windows.Controls.Label
$passwordLabel.Content = "Password:"
$passwordLabel.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Right
$passwordLabel.VerticalAlignment = [System.Windows.VerticalAlignment]::Center
$passwordBox = New-Object System.Windows.Controls.PasswordBox
$passwordBox.Name = "PasswordBox"
$passwordBox.Margin = New-Object System.Windows.Thickness(5, 2, 150, 2)
$passwordBox.SetValue([System.Windows.Controls.Grid]::ColumnProperty, 1)
$passwordGrid.Children.Add($passwordLabel) | Out-Null
$passwordGrid.Children.Add($passwordBox) | Out-Null
$paramPanel.Children.Add($passwordGrid) | Out-Null

# Join button
$runScript = New-Object System.Windows.Controls.Button
$runScript.Content = 'Join Local AD Domain'
$runScript.Height = 25
$runScript.Width = 130
$runScript.IsEnabled = $true
$runScript.SetValue([System.Windows.Controls.Grid]::RowProperty, 3)
$runScript.add_Click({ JoinButton_Click })
$mainGrid.Children.Add($runScript) | Out-Null

# Output section
$outputTextBox = New-Object System.Windows.Controls.TextBox
$outputTextBox.AcceptsReturn = $true
$outputTextBox.IsReadOnly = $true
$outputTextBox.VerticalScrollBarVisibility = [System.Windows.Controls.ScrollBarVisibility]::Visible
$outputTextBox.HorizontalScrollBarVisibility = [System.Windows.Controls.ScrollBarVisibility]::Auto
$outputTextBox.SetValue([System.Windows.Controls.Grid]::RowProperty, 4)
$mainGrid.Children.Add($outputTextBox) | Out-Null

# Footer
$Footer = New-Object System.Windows.Controls.TextBlock
$Footer.Text = "   Join local AD GUI | version 0.0.04 | www.ez.be"
$Footer.FontSize = 10
$Footer.HorizontalAlignment = [System.Windows.HorizontalAlignment]::Left
$Footer.VerticalAlignment = [System.Windows.VerticalAlignment]::Center
$Footer.SetValue([System.Windows.Controls.Grid]::RowProperty, 5)
$mainGrid.Children.Add($Footer) | Out-Null

# Show the window
$window.ShowDialog()


<#
.SYNOPSIS
GUI script for joining a machine to a local AD domain.

.DESCRIPTION
This script creates a GUI window that prompts the user to enter the domain name, username, and password to join the local machine to a domain. 
If the domain is joined successfully, the output is displayed in a textbox.

.PARAMETER None

.EXAMPLE
JoinButton_Click

.NOTES
Author: Jurgen Verhelst | ez Networking | www.ez.be
The script requires the following .NET assemblies: PresentationFramework, PresentationCore, WindowsBase, WindowsFormsIntegration.
#>
